import React, { useState, useEffect } from 'react'
import { useSelector, useDispatch } from 'react-redux'
import { getAddress } from "../../redux/actions/AddressCompAction"

const InputScreen = () => {
    const [origin, setOrigen] = useState()
    const [originLatLan, setOriginLatLan] = useState({})

    const bigData = useSelector(state => state.addressReducer)
    const {loading, error, data} = bigData

    const dispatch = useDispatch()
    const onChangeHandler1 = (key) => {
        setOrigen(key)
        if(Object.keys(bigData).length !==1){
            setOriginLatLan({
                lat: data.features[0].geometry.coordinates[0],
                lan:data.features[0].geometry.coordinates[1]
            })
        }

    }
    useEffect(() => {
        dispatch(getAddress(origin))
    }, [origin])

    const clickhandler = ()=>{
        console.log(originLatLan)
    }
    return (
        <div className="input-layout">
            <input type="text" onChange={(e) => onChangeHandler1(e.target.value)} />
            <button onClick={clickhandler}>click</button>
        </div>
    )
}

export default InputScreen
