import React, { useState, useEffect } from 'react'
import {useDispatch } from 'react-redux'
import { getAddress } from "../../redux/actions/AddressCompAction"

const InputScreen = () => {
    const [firstAddress, setFirstAddress] = useState()
    const [secondAddress, setSecondAddress] = useState()
    const [date, setDate] = useState({})
    const dispatch = useDispatch()

    const onChangeHandler1 = (value) => {
        setFirstAddress(value)
    }
    const onChangeHandler2 = (value) => {
        setSecondAddress(value)
    }


    useEffect(() => {
        dispatch(getAddress(firstAddress, secondAddress))

    }, [dispatch, firstAddress, secondAddress, date])


    return (
        <div className="input-layout">
            <input type="text" onChange={(e) => onChangeHandler1(e.target.value)} />
            <input type="text" onChange={(e) => onChangeHandler2(e.target.value)} />

        </div>
    )
}

export default InputScreen
